// Create a test driver application for admin verification demo
const testApplication = {
  id: Date.now(),
  fullName: 'Test Driver',
  email: 'test.driver@example.com',
  phone: '+27123456999',
  licenseNumber: 'DL987654321',
  licenseExpiry: '2025-12-31',
  towTruckType: 'flatbed',
  vehicleRegistration: 'TEST-123-GP',
  status: 'pending',
  submittedAt: new Date().toISOString(),
  documents: {
    driverLicenseFront: 'data:image/jpeg;base64,',
    driverLicenseBack: 'data:image/jpeg;base64,',
    vehicleRegistrationDoc: 'data:image/jpeg;base64,',
    profilePhoto: 'data:image/jpeg;base64,'
  }
};

// Add to localStorage
const existingApps = JSON.parse(localStorage.getItem('driver_applications') || '[]');
existingApps.push(testApplication);
localStorage.setItem('driver_applications', JSON.stringify(existingApps));

console.log('Test driver application created!');
console.log('Application ID:', testApplication.id);
console.log('Check admin dashboard at http://localhost:3001');